        public static string ReplaceGuid(string gridXml, string tag, string guid)
        {
            return ReplaceGuid(gridXml, tag, -1, guid);
        }

        public static string ReplaceGuid(string gridXml, string tag, int gap, string guid)
        {
            if (gap < 0)
            {
                gap = 6;
            }
            string gridXml2 = gridXml.Remove(gridXml.IndexOf(tag) + tag.Length + gap, 36);
            string gridXml3 = gridXml2.Insert(gridXml.IndexOf(tag) + tag.Length + gap, guid);
            return gridXml3;

        }

        /// <summary>
        /// Help method to create a gridXml file that returns a specific page view
        /// By default, it will be the first page view
        /// Grid xml is expected to be passed in decoded format
        /// </summary>
        /// <param name="_gridXml">decoded grid XML</param>
        /// <param name="pageNumber">the page number to be viewed</param>
        /// <param name="viewId">view Id</param>
        /// <param name="pagingCookie">paging cookie</param>
        /// <returns></returns>
        public static string gridXmlhelp(string _gridXml, int pageNumber, string viewId, string pagingCookie)
        {
            string gridXml = _gridXml;
            //reset the view Id
            gridXml = gridXml.Remove(gridXml.IndexOf("<viewid>") + 14, 36);
            gridXml = gridXml.Insert(gridXml.IndexOf("<viewid>") + 14, viewId);
            if (pagingCookie != null)
            {
                //gridXml = gridXml.Replace("<pagingCookie>", "<pagingCookie>" + pagingCookie);
                int length = gridXml.IndexOf("</pagingCookie>") - gridXml.IndexOf("<pagingCookie>") - 14;
                if (length > 0)
                {
                    string oldPagingCookie = gridXml.Substring(gridXml.IndexOf("<pagingCookie>") + 14, length);
                    gridXml = gridXml.Replace(oldPagingCookie, pagingCookie);
                }
            }
            if (pageNumber != -1)
            {
                gridXml = gridXml.Replace("<pageNum>1", "<pageNum>" + pageNumber.ToString());
            }
            return gridXml;
        }


        /// <summary>
        /// Help method to create a gridXml file that returns a specific page view
        /// By default, it will be the first page view
        /// Grid xml is expected to be passed in decoded format
        /// </summary>
        /// <param name="_gridXml">decoded grid XML</param>
        /// <param name="pageNumber">the page number to be viewed</param>
        /// <param name="viewId">view Id</param>
        /// <param name="pagingCookie">paging cookie</param>
        /// <returns></returns>
        public static string gridFilterXmlhelp(string _gridXml, int pageNumber, string viewId, string viewXml, string filterXml, string pagingCookie)
        {
            string gridXml = _gridXml;
            //reset the view Id
            gridXml = gridXml.Remove(gridXml.IndexOf("<viewid>") + 14, 36);
            gridXml = gridXml.Insert(gridXml.IndexOf("<viewid>") + 14, viewId);
            pagingCookie = HttpUtility.HtmlDecode(pagingCookie);
            if (pagingCookie != null)
            {
                int length = gridXml.IndexOf("</pagingCookie>") - gridXml.IndexOf("<pagingCookie>") - 14;
                string oldPagingCookie = gridXml.Substring(gridXml.IndexOf("<pagingCookie>") + 14, length);
                gridXml = gridXml.Replace(oldPagingCookie, pagingCookie);
            }
            if (pageNumber != -1)
            {
                gridXml = gridXml.Replace("<pageNum>1", "<pageNum>" + pageNumber.ToString());
            }

            XmlDocument doc = new XmlDocument();
            doc.LoadXml(gridXml);

            XmlNode fetchNode = doc.SelectSingleNode("/grid/parameters/fetchXmlForFilters");
            fetchNode.InnerText = viewXml;
            XmlNode filterNode = doc.SelectSingleNode("/grid/parameters/effectiveFetchXml");
            filterNode.InnerText = filterXml;
            gridXml = doc.InnerXml;

            return gridXml;
        }

