	public class SearchHelper
	{
		public static string GenerateRandomText(int size)
		{
			var random = new Random();
			const string chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

			var buffer = new char[size];

			for (var i = 0; i < size; i++)
			{
				buffer[i] = chars[random.Next(chars.Length)];
			}

			return new string(buffer);
		}

		public static string GetKnowledgeArticleTitle()
		{
			var kmRequest = new EntityRequest
			{
				Type = EntityNames.KnowledgeArticle,
				ReturnAs = EntityNames.KnowledgeArticle
			};

			var crmEntity = EntityManager.Instance.GetEntities(kmRequest);
			var items = (CRMEntity)crmEntity[EntityNames.KnowledgeArticle];

			return items["title"];
		}

		public static string GetSearchText(int searchType)
		{
			switch (searchType)
			{
				case 1:
					return GetKnowledgeArticleTitle();
				case 2:
					return GenerateRandomText(new Random().Next(1, 4));
				default:
					return "general";
			}
		}
	}
